void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float isSin = uv.y == 0.5 ? 1.0 : 0.0;

    float s = sin(2.0*3.14*uv.x - iTime*2.0);

    float dx = 1.0 - abs(s);
    float dy = abs(uv.y - 0.5 + dx*sign(s)*1.3);
    float d = dy*dy+dx*dx;
    fragColor = vec4(0.5+0.2*sin(iTime), 0.2+0.2*sin(iTime), 0.3 + 0.2*cos(iTime), 1.0);
    fragColor += abs(uv.y*2.0-1.0 - s) < 0.1 ? vec4(vec3(cos(iTime*0.25*sin(uv.x))), 1.0) + vec4(0.8, 0.0, -0.5, 1.0) : vec4(0.0);
    fragColor += d < 0.025 ? vec4(vec3(sin(iTime*d*cos(uv.x*uv.y*20.0))), 1.0) + vec4(-0.8, 0.0, 0.5, 1.0): vec4(0.0);
}
