void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // two points
    vec2 A = vec2(100,100);
    vec2 B = vec2(iMouse.xy);

    // direction
    vec2 s = B-A;
    // normal vector
    vec2 n = vec2(-s.y,s.x);

    n = normalize(n);

    // ax + by + c = 0
    // p = (a,b,c)
    vec3 p = vec3(n.x,n.y,-dot(n,A));
    // ax + by + c = 0
    // c = -ax-by
    // -(nx*Ax + ny+Ay)

    // (a,b,c) * (fx,fy,1) = afx + bfy + c
    float d = dot(p,vec3(fragCoord.xy,1));

    fragColor = vec4(vec3(abs(d)),1.f);
}
