float drawline(vec2 P,vec2 A, vec2 B, float r){

    vec2 g = B - A;
    vec2 h = P - A;
    float c = length(h-g*clamp(dot(g,h) / dot(g,g), 0.0,1.0));
    return smoothstep(r, .01/r,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // color
    vec3 background = vec3(0.22,0.34,0.51);
    vec3 finalcolor = vec3(0.0);

    // math
    vec2 uv =  vec2(fragCoord * 2. - iResolution.xy) / iResolution.y;
    float radius = 1.5;
    //line
    vec2 dline = vec2(sin(iTime),cos(iTime))*.5*radius;
    float line = drawline(uv,vec2(0,0),dline,0.5);
    //circle
    float d = 0.01/ abs(length(uv*2.)/radius -1.)*2.5;

    d = step(1.0,d);
    line = step(1.0,line);

    finalcolor = background + vec3(line + d);

    fragColor = vec4(finalcolor, 1.0);
}
