#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    for(float i=0.;i<5.;i++){
        uv.x=abs(sin(uv.x*3.+iTime));
        uv*=rot(iTime*i);
    }

    vec2 center = vec2(0);
    center += center * .5;
    float d = distance(uv,center);

    float r = .5;
    if(d<r){
    fragColor = vec4(.2,
        sin(iTime*8. + uv.x*8.)*.5+.5,
        cos(iTime*8. + uv.y*8.)*.5+.5,
        .2);
    } else {fragColor = vec4(.3,
        sin(iTime*8. + uv.x*8.)*.5+.5,
        cos(iTime*8. + uv.y*8.)*.5+.5,
        .2);}

}
