void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.;
    fragCoord /= zoom;

    vec3 col = texelFetch(iChannel0, ivec2(fragCoord), 0).xyz;

    col = clamp(col,0.,1.);
    fragColor = vec4(col, 1.0);
}

#define prev(X,Y) ( texelFetch(iChannel0, ivec2(fragCoord)+ivec2(X,Y), 0) )
#define noise(X,Y) ( texture(iChannel1, fract(uv+vec2(X,Y))) )

#define RED vec4(1.,0.,0.,0.)
#define GREEN vec4(0.,1.,0.,0.)
#define BLUE vec4(0.,0.,1.,0.)
#define WHITE vec4(1.,1.,1.,0.)
#define BLACK vec4(0.,0.,0.,0.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec4 col = prev(0,0);
    mat3x3 loosesTo = mat3x3(0,1,0, 0,0,1, 1,0,0);

    int iterationEachNFrames = 1;

    if (iFrame % iterationEachNFrames == 0) {
        vec4 neigh = prev(1,0)+prev(1,1)+prev(0,1)+prev(-1,1)+prev(-1,0)+prev(-1,-1)+prev(0,-1)+prev(1,-1);
        float L = step(2.5, dot(col.rgb * loosesTo, neigh.rgb));
        col = vec4(L*col.xyz*loosesTo+(1.-L)*col.xyz, 0.);
    }

    // Initial condition
    if (iFrame < 5) {
        col = RED*step(0.5,noise(0,0).x) + GREEN*step(1./3.,noise(0,0).x)*step(noise(0,0).x,0.5) + BLUE*step(noise(0,0).x,1./3.); 
    }

    fragColor = col;
}
