float circle(vec2 p, float r)
{
    return length(p)-r;
}

float donut(vec2 p, vec2 s)
{
    float d = abs(circle(p, s.x))-s.y;
    return d>0.?0.:1.;
}

float line(float l, float lt)
{
    return 1.-smoothstep(0., lt, abs(l));
}

float rotate(float v, float a)
{
    float r = radians(a);
    return v * cos(r);
}

float rotated_line(vec2 uv, float a, float lt)
{
    float r = radians(a);
    float rot = uv.x*cos(r)-uv.y*sin(r);
    return 1.-smoothstep(0., lt, abs(rot));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 cp = uv-vec2(0);

    vec3 bg = vec3(245, 245, 242)/255.;
    vec3 y = vec3(235, 230, 110)/255.;
    vec3 g = vec3(150, 222, 200)/255.;
    vec3 b = vec3(64)/255.;
    float lt = 0.003;
    float lt2 = 0.01;

    float dl = uv.x<=0.?donut(cp, vec2(0.6, 0.15)):0.;
    float dr = uv.x>=0.?donut(cp, vec2(0.57, 0.12)):0.;
    vec3 col = mix(bg, y*dl+g*dr, dl+dr);

    float l = line(circle(cp, 0.45), lt);
    l += uv.x<=0.?line(circle(cp, 0.6), lt):0.;
    l += uv.x<=0.?line(circle(cp, 0.75), lt):0.;
    l += uv.x>=0.?line(circle(cp, 0.575), lt):0.;
    l += uv.x>=0.?line(circle(cp, 0.69), lt):0.;
    l += abs(uv.y)>0.45&&abs(uv.y)<0.75?line(uv.x, lt):0.;
    l += uv.y>rotate(0.45, -60.)&&uv.y<rotate(0.75, -60.)?rotated_line(uv, -60., lt):0.;
    l += uv.y<rotate(-0.45, 60.)&&uv.y>rotate(-0.75, 60.)?rotated_line(uv, 60., lt):0.;
    l += uv.y>rotate(0.45, 36.)&&uv.y<rotate(0.69, 36.)?rotated_line(uv, 36., lt):0.;
    l += uv.y>rotate(0.45, 72.)&&uv.y<rotate(0.69, 72.)?rotated_line(uv, 72., lt):0.;
    l += uv.y<rotate(0.45, 108.)&&uv.y>rotate(0.69, 108.)?rotated_line(uv, 108., lt):0.;
    l += uv.y<rotate(0.45, 144.)&&uv.y>rotate(0.69, 144.)?rotated_line(uv, 144., lt):0.;

    float a = atan(-cp.y, -cp.x);
    float f1 = smoothstep(-5.,5., cos(a*5.))*.7+0.22;
    l += uv.x>=0.?line(circle(cp, f1), lt2):0.;

    float f2 = smoothstep(-5.,5., cos(a*3.))*0.93+0.135;
    l += uv.x<=0.?line(circle(cp, f2), lt2):0.;

    col = mix(col, b, l);
    fragColor = vec4(col,1.0);
}
