// Digimind's 256-byte intro "Drinking Fountain" converted from x86 assembly.
// https://www.pouet.net/prod.php?which=96016

// All credits go to Digimind.

float sq(float x) { return x*x; }
float cospi(float x) { return cos(3.14159265 * x); }

void mainImage(out vec4 O, vec2 X) {
    vec2 R = iResolution.xy,
         U = (X+X - R) / R.y / log(2.);  // y = -1.442695 .. 1.442695
    float x = U.x * 0.75,                // adjust aspect ratio
          y = U.y,
          t = 1. - y,
          b = t*t*t*t - x - x,
          r = 1. / sqrt(x*x + y*y + 1.);
    x *= r; y *= r;

    for (int i=11; i>0; i--) {
        if (i == 4) {
            if (b > 2.) x = 1.;
            x += 19.; r += 41.;
        }
        x -= r++; t = sq(cospi(r)) * sin(sq(round(r)));
        r = y; y = x; x = cospi(t) * cos(sq(round(t)));
    }
    O = y * vec4(4,5,6,0) * 19./256.;
}
