#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;

    vec2 center = vec2(0);
    float d = distance(uv, center);

    float r = 0.5;
    if (d < r)
    {
        vec3 color = vec3(1.0, 0.5 + 0.5 * sin(iTime * 2.0), 0.5 + 0.5 * cos(iTime * 2.0));
        float colorFactor = smoothstep(0.0, r, d);
        vec3 finalColor = mix(vec3(1.0), color, colorFactor);

        fragColor = vec4(finalColor, 1.0);
    }
    else
    {
        fragColor = vec4(0.0);
    }
}
