vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb);
	return c.z * mix( vec3(1.0), rgb, c.y);}
//smooth hsv from iq https://www.shadertoy.com/view/MsS3Wc

vec2 pMult(vec2 a, vec2 b){return vec2(a.x*b.x-a.y*b.y,-a.x*b.y-a.y*b.x);}

vec2 targ(float a){
float b=mod(a,32.);
if(b<4.){return vec2(-1.7320508,0.);}
if(b<8.){return mix(vec2(-1.7320508,0.),vec2(-1.39958,.07112),pow((b-4.)*.25,7.));}
if(b<12.){return vec2(-1.39958,.07112);}
if(b<16.){return mix(vec2(-1.39958,.07112),vec2(-1.4016,.0),pow((b-12.)*.25,7.));}
if(b<20.){return vec2(-1.41667,0.);}
if(b<24.){return mix(vec2(-1.41667,0.),vec2(-1.1349,.09002),pow((b-20.)*.25,7.));}
if(b<28.){return vec2(-1.1349,.09002);}
return mix(vec2(-1.1349,.09002),vec2(-1.7320508,0.),pow((b-28.)*.25,7.));
}

