renderpass.code:
#define h21(p) fract(sin(dot(p, vec2(12.34,26.534))) * 987.23)
#define t iTime
#define pi acos(-1.)
#define TAU (2.* pi)

void mainImage(out vec4 o, vec2 u) {
    vec2 k, q, p, r = iResolution.xy;
    float i, d, coin;
    u = 4. * (u - r / 2.) / r.y;
    o *= 0.;
    p = u;

    //if(false)
    p = vec2(
           atan(p.x, p.y) + cos(t * .1) * .4,
           log(length(p)) + sin(t * .2) * .3
       );

    p = fract(p / TAU) * 30.;
    q = floor(p);
    p = fract(p) - .5;

    coin = h21(q) > .5 ? 1. : -1.;

    d = abs(p.x - coin * p.y) - .15; // maze

    o += (1. - length(p)) * .5 ; // bg


    k = p + cos( // dots
                iTime + h21(q) * 6.28
            ) * vec2(1, coin) * .4;

    o += smoothstep(15. / r.y, 0., d) * smoothstep(0., 15. / r.y, length(k) - .08) ;
}
