void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2. * (fragCoord - iResolution.xy/2.)/iResolution.x;

    vec3 col = vec3(.3);

    for(float i = 0.; i < 20.; i++)
    {    
        vec2 testpoint = texelFetch(iChannel0, ivec2(i, 0), 0).xy;
        col += smoothstep(0.006, .0,  sdCircleLine(uv - testpoint, .06, .001));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

float dot2( in vec2 v ) { return dot(v,v); }
float dot2( in vec3 v ) { return dot(v,v); }
float ndot( in vec2 a, in vec2 b ) { return a.x*b.x - a.y*b.y; }

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdCircleLine(in vec2 p, in float r, in float w)
{
    return abs(sdCircle(p, r)) - w;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);
    float b = sqrt(r*r-d*d);
    return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                             : length(p-vec2(-d,0.0))-r;
}

float sdBezier( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
    float kk = 1.0/dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      
    float res = 0.0;
    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx-3.0*ky) + kz;
    float h = q*q + 4.0*p3;
    if( h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = clamp( uv.x+uv.y-kx, 0.0, 1.0 );
        res = dot2(d + (c + b*t)*t);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp(vec3(m+m,-n-m,n-m)*z-kx,0.0,1.0);
        res = min( dot2(d+(c+b*t.x)*t.x),
                   dot2(d+(c+b*t.y)*t.y) );
        // the third root cannot be the closest
        // res = min(res,dot2(d+(c+b*t.z)*t.z));
    }
    return sqrt( res );
}

// 0,0 = control point
// x,0,= other points
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    if(ivec2(fragCoord.xy) == ivec2(0))
    {
        if(iMouse.z > 0.)
        {
            vec2 mpoint = 2. * (iMouse.xy - iResolution.xy/2.)/iResolution.x;

            float d = length(mpoint - fragColor.xy);
            vec2 a = normalize(mpoint - fragColor.xy)*.003;

            fragColor.zw = mix(fragColor.zw*.9, fragColor.zw + a, smoothstep(0., .2, d));

            fragColor.zw = clamp(fragColor.zw, -.1, .1);
        }
        else
        {
            fragColor.zw = .9*fragColor.zw;
        }
        fragColor.xy = fragColor.xy + fragColor.zw;
        // todo clamp to within a specific distance from mouse

    }
    else if(fragCoord.y < 2.)
    {
        float d = .01;
        // clamp to a specific distance away from 
        vec2 anchor = texelFetch(iChannel0, ivec2(fragCoord.x - 1., 0), 0).xy;
        vec2 a = normalize(fragColor.xy - anchor);
        fragColor.xy = anchor + d * a; 
    }

    // init
    if(iFrame < 4)
    {
        fragColor = vec4(fragCoord.x, 0., 0., 0.);
    }
}
