void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // settings
    bool enableDistortion = true;


    bool enableAnimation = true;
    float animationSpeed = 1.0;
    float animationIntensity = 1.0;

    float intensity = 1.;


    if (enableAnimation) {
        animationIntensity = sin(iTime * animationSpeed);
    }

    float amount = texture(iChannel0, uv).r * 0.1 * animationIntensity * intensity;
    vec3 col;
    col.r = texture( iChannel1, vec2(uv.x + amount, uv.y) ).r;
    if (enableDistortion) {
        col.g = texture( iChannel1, vec2(uv.x, uv.y + amount) ).g;
    } else {
        col.g = texture( iChannel1, uv ).g;
    }
    col.b = texture( iChannel1, vec2(uv.x - amount, uv.y) ).b;
    fragColor = vec4(col, 1.0);
}

const float pi = 3.14;

float random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );

    return -1.0 + 2.0 * fract( sin( dot( st.xy, vec2(12.9898,78.233) ) ) * 43758.5453123);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/lsf3WH
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( random2( i + vec2(0.0,0.0) ), 
                     random2( i + vec2(1.0,0.0) ), u.x),
                mix( random2( i + vec2(0.0,1.0) ), 
                     random2( i + vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    float n = noise(uv * 6.);
    float v1 = cos((uv.x + n * 0.03) * 30.0) * 0.5 + 0.7;
	float v2 = cos((uv.y + n * 0.08) * 60.) * 0.5 + 0.5;
	float v = n * v1 * v2;
    fragColor = vec4(v, v, v, 1.0);
}
