void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 u = ( fragCoord - .5* iResolution.xy ) /iResolution.y;
   float d = length(u);
   float i = clamp(round(d * 20.),4.,9.);     
   float g = .002 + .006*sin(i*6.28318*fract(.1*iTime) + atan(u.y, u.x) + i);
   fragColor = vec4(vec3(0)+g/(abs(d-i/20.)),1);

}
