void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * 0.5;
    float p = 0.0;
    p += sin(uv.x * 10.0 + t * 3.0) * 0.5;
    p += cos(uv.y * 10.0 - t * 3.0) * 0.5;

    float radial_p = length(uv);
    radial_p = tan(radial_p * 10.0 - t * 2.0) * 0.5 + 0.5;

    p = mix(p, radial_p, 0.6);

    vec3 color;
    if (p < 0.1) {
        color = vec3(0.2, 0.0, 0.4);
    }
    else if (p < 0.6) {
        color = vec3(0.5, 0.0, 0.7);
    }
    else {
        color = vec3(0.8, 0.0, 1.0);
    }

    float pulse = tan(t * 2.0) * 0.1 + 0.9;
    color *= pulse; 

    fragColor = vec4(color, 1.0);
}
