void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r,g,b,s,f,d,movx,movz,movy,z;

    float x=(fragCoord.x/iResolution.x-0.5)*(1.+(iResolution.y/iResolution.x));
    float y=(fragCoord.y/iResolution.y-0.5);

    z=(10.+sin(iTime)*5.);
    movz=z+sin(iTime/1.3)*15.;
    movx=sin(iTime)*10.;
    movy=cos(iTime/2.1)*10.;

    g=(x*x+y*y)*z;
    r=sin((x*(z+g))+movx)*cos((y*(z+g))+movy);

    b= 1.-g/(1.-r);





    fragColor = vec4(b,b,b,1.0);
}
