void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.2; 

    float radius = length(uv) * 4.0;
    float angle = atan(uv.y, uv.x) + sin(radius * 5.0 - time * 4.0);

    float spiral = sin(angle * 20.0 + radius * 5.0 - time * 10.0) * 0.5 + 0.5;
    float floralPattern = smoothstep(0.45, 0.55, sin(radius * 12.0 + angle * 12.0 + time * 5.0));

    vec3 color = mix(vec3(1.0, 0.8, 0.0), vec3(1.0, 0.0, 0.0), spiral);
    color = mix(color, vec3(0.0, 0.0, 0.0), floralPattern);

    float grad = sin(radius * 10.0 - time * 2.0) * 0.5 + 0.5;
    color = mix(color, vec3(1.0, 1.0, 1.0), grad * 0.2);

    fragColor = vec4(color, 1.0);
}
