// by KilatAmor
#define pi acos(-1.)
#define s(a) .09*sin(kf*a)
#define L length
#define c(a) d+.2*sin(T*a)
#define SS(a) smoothstep(.33,.36,d+a);

void mainImage( out vec4 O, in vec2 F )
{
    vec2 uv = (F * 2. - iResolution.xy) / iResolution.y, uv1,uv2;
    float  d, kf, l,  T=iTime;
    float sk=abs(.1+sin(T*6.));
    float k = .7+sk*.3;
    uv.x+=.2*sin(T*3.);
    vec3 col;
    l=L(uv);

    if (l<=k)
    {
        uv2=uv1=normalize(uv)*sin(l*pi*.5)*sin(l*pi*.5);
        kf=100.*(sk+1.);
        uv2+=vec2(s(uv.x),s(uv.y));
    }
    else
    {
        uv2=uv1=normalize(uv)*sqrt(sqrt(l)/l*(.7+.4*sk));
        kf=160.+40.*sk;
        uv2+=vec2(s(uv.x),s(uv.y));
    }

    uv2.y+=cos(uv2.x*cos(uv2.x+T*.5)-T);
    d=fract(1.*L(cos(uv2-T*.2)+2.*sin(uv2+T*.4))*(1.5+.2*sin(T)) - T*1.2);
    d=SS(-.2);
    if (d<=.2)
    {
        uv1.y+=sin(uv1.x*cos(uv1.x+T*.7)+T);
        float d = L(sin(uv1+T*.8)+2.*cos(uv1+T*.43));
        d = abs(fract(d*(1.4+.3*sin(T)) - T*1.5) - (.7+.2*sin(T)) );
        d=SS(0.);
        d*=.35+.8*sin(1.9-L(uv));
        d*=sin(d);
        col = vec3(c(.7),c(.9),c(1.2));    
    }
    else
    {
        d+=.05*sin(T*5.);
        d*=.15+.8*sin(1.9-L(uv));
        d*=d;
        col = vec3(c(1.),c(1.25),c(1.5));    
    }

    if (l>k) col*=.5+(l-k)*5.*k;
    else
    {
        vec2 uvs = uv*(1.9-k*k);
        float d1=L((uvs+vec2(-.2*sin(T*3.),-.5))*vec2(.8,1.3)),d2=L((uvs+vec2(0.,-.5))*vec2(.9,.9));
        float dd2 = 1.3*clamp(d1,0.,.5) * clamp(d2,.4,1.);
        float cl = (1.-dd2);
        cl*=cl;    
        col*=cl*cl+.2;
        col+=cl*cl;
    }

    // Output to screen
    O = vec4(col,1.);
}
