#define PI 3.141592653
const int size = 6;
const vec3 colorArr[size] = vec3[](vec3(0.88,0.89,0.92), vec3(0.82,0.76,0.84), vec3(0.52,0.44,0.61), vec3(0.27,0.28,0.47), vec3(0.36,0.49,0.70), vec3(0.71,0.79,0.84));
const float area_num = 100.0;

float getAngle(vec2 uv, vec2 center, vec2 start){
    vec2 a = start - center;
    vec2 b = uv - center;
    float cosTheta = dot(a,b)/length(a)/length(b);
    float theta = acos(cosTheta);
    float dir = a.x*b.y-a.y*b.x;
    if(dir<=1e-6){
        theta = abs(theta) + PI;
    }
    return theta;
}

vec2 rotate(vec2 p, float theta){
    float x = p.x*cos(theta) - p.y*sin(theta);
    float y = p.x*sin(theta) + p.y*cos(theta);
    return vec2(x,y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.x;
    //uv = fract(uv*area_num);
    vec2 center = vec2(0.0, 0.0);
    vec2 start = vec2(1.0, 0.0);
    start = rotate(start,iTime*0.2);
    vec3 color;

    //float x_step = 1.0/float(area_num);
    //int index = int(floor(uv.x/x_step));
    float angle_step = 2.0*PI/float(area_num);
    int index = int(floor(getAngle(uv,center,start)/angle_step));

    int i = int(mod(float(index),float(size)));
    color = colorArr[i];

    fragColor = vec4(color,1.0);
}
