highp float rainbow(vec2 uv, highp float offset)
{
    float xmod = uv.x + -sin(iTime*.05) * 10.;
    float ymod = uv.y + sin(iTime * .5) * .5;
    if(ymod>0.) ymod = 100.;
    return(cos(xmod / ymod + 4. * iTime + offset));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 col = vec3(rainbow(uv, 0.), rainbow(uv, 1.), rainbow(uv, 2.));
    fragColor = vec4(col, 1.0);
}
