#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 b = B(u);
    fragColor = sqrt(vec4(0,(b.xy+.5)*length(b.xy),0))*2.;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
float customa = .28;
float customb = .22;
float customc = 1.19;
float wave(float l)
{
    float t   = 8.*customa;      //how expanded is the wave
    float wid = 4.*customb;      //wave width
    float c   = 1./wid;
    return 1./l*(-(t - l)*exp(-pow(t - l,2.)/c/c)
                 +(t + l)*exp(-pow(t + l,2.)/c/c));
}
void mainImage( out vec4 fragColor, in vec2 u )
{
    float rZ = 6.;
    float et = 0.;
    vec2 d  = B(u).xy;
    vec2 v1 = vec2(0);
    vec2 v2 = vec2(0);
    for(float y=-rZ; y<=rZ; y+=1.){
    for(float x=-rZ; x<=rZ; x+=1.){
        vec2 xy = vec2(x,y);
        float l2 = dot(xy,xy);
        if(l2<.1 || l2>rZ*rZ+.1){continue;}
        vec2 d2 = B(u+xy).xy;
        float l = sqrt(l2);
        float e  = wave(l);  et+=e*e;
        v1 += pow(dot(d2-d,vec2( x,y)/l)*e,2.);//abs divergence
        v2 += pow(dot(d2-d,vec2(-y,x)/l)*e,2.);//abs curl
    }}
    fragColor = vec4(sqrt(v1/et),sqrt(v2/et));
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
float customa = .28;
float customb = .22;
float customc = 1.19;
float wave(float l)
{
    float t   = 8.*customa;      //how expanded is the wave
    float wid = 4.*customb;      //wave width
    float c   = 1./wid;
    return 1./l*(-(t - l)*exp(-pow(t - l,2.)/c/c)
                 +(t + l)*exp(-pow(t + l,2.)/c/c));
}
void mainImage( out vec4 fragColor, in vec2 u )
{
    float rZ = 6.;
    vec2 d  = B(u).xy;
    vec2 v  = vec2(0);
    float et = 0.;
    for(float y=-rZ; y<=rZ; y+=1.){
    for(float x=-rZ; x<=rZ; x+=1.){
        vec2  xy = vec2(x,y);
        float l2 = dot(xy,xy);
        if(l2<.1 || l2>rZ*rZ+.1){continue;}
        vec2 c2 = A(u+xy).zw;
        float l  = sqrt(l2);
        float e  = wave(l);  et+=abs(e);
        v += xy/l*c2*e;
    }}
    if(iFrame==0)
    {
        vec2 r = vec2(dot(u, vec2(14.9898, 78.85638)),
                      dot(u, vec2(27.237247, 163.87563)));
             r = fract(sin(r) * vec2(43758.5453,23625.753281));
        vec2 k = 2.*(u*2.-iResolution.xy)/iResolution.y;
        v = normalize(k)*.2;
        //v = -8f*u/exp(dot(u,u));
        v = 22.*r;///exp(dot(k,k));
    }
    if(iMouse.z>0.)
    {
        vec2 m = 8.*(u-iMouse.xy)/iResolution.y;
        v *= 1.-exp(-dot(m,m));
    }
    fragColor = vec4(d+(v/et*customc*22.-d)*.3,0,0);
    //fragColor = d+ v/et*customc*4.;
}
