// More point containment queries here: https://www.shadertoy.com/playlist/fXdfzX

#if HW_PERFORMANCE==0
#define NEWTON_ITERATIONS 2
#define NUM_STEPS 8
#else
#define NEWTON_ITERATIONS 2
#define NUM_STEPS 32    
#endif

#define CONVERGED_ERROR 1e-3
#define DIVERGED_ERROR 1e6

#define EXPOSURE 2.5

// Node positions, with "y" up and data values stored in "w" 
struct Pyramid {
    // Bottom quad (counter-clockwise order)
    vec4 v0, v1, v2, v3;

    // Top point
    vec4 v4;
};

bool interpolate(vec3 P, in Pyramid e, out float value) {
    // Initialize our canonical coordinates to be in the center of the element
    // We'll iteratively refine these with Newton's method to match the given "P"
    vec3 rst_curr = vec3(0.5, 0.5, 0.2);
    vec3 rst = vec3(0.5, 0.5, 0.2);

    // The final support function values
    float w0, w1, w2, w3, w4;

    // Iteration for Newton's method
    bool converged = false;
    for (int iteration = 0; iteration < NEWTON_ITERATIONS; iteration++) {
        vec3 rstm = 1.0 - rst;

        // Compute support function values for the current position in "rst" space
        w0 = rstm.x * rstm.y * rstm.z;
        w1 = rst.x * rstm.y * rstm.z;
        w2 = rst.x * rst.y * rstm.z;
        w3 = rstm.x * rst.y * rstm.z;
        w4 = rst.z;

        // If the "rst" location matches the query point, we can now use our support function values
        if (converged) break;

        // Determine if the current cannonical coordinates accurately reflect the given query point
        vec3 fcol = e.v0.xyz * w0 + e.v1.xyz * w1 + e.v2.xyz * w2 + e.v3.xyz * w3 + e.v4.xyz * w4;
        fcol -= P; // World space position relative to the given query point. We want this to be zero.

        // rst-derivatives
        vec3 dw0 = vec3( -(rst.y - 1.f) * (rst.z - 1.f), -(rst.x - 1.f) * (rst.z - 1.f), -(rst.x - 1.f) * (rst.y - 1.f));
        vec3 dw1 = vec3( (rst.y - 1.f) * (rst.z - 1.f), rst.x * (rst.z - 1.f), rst.x * (rst.y - 1.f));
        vec3 dw2 = vec3( rst.y - rst.y * rst.z, rst.x - rst.x * rst.z, -rst.x * rst.y);
        vec3 dw3 = vec3( rst.y * (rst.z - 1.f), (rst.x - 1.f) * (rst.z - 1.f), (rst.x - 1.f) * rst.y);
        vec3 dw4 = vec3( 0.f, 0.f, 1.f);

        // Now get derivatives in world space
        vec3 rcol = e.v0.xyz * dw0.x + e.v1.xyz * dw1.x + e.v2.xyz * dw2.x + e.v3.xyz * dw3.x + e.v4.xyz * dw4.x;
        vec3 scol = e.v0.xyz * dw0.y + e.v1.xyz * dw1.y + e.v2.xyz * dw2.y + e.v3.xyz * dw3.y + e.v4.xyz * dw4.y;
        vec3 tcol = e.v0.xyz * dw0.z + e.v1.xyz * dw1.z + e.v2.xyz * dw2.z + e.v3.xyz * dw3.z + e.v4.xyz * dw4.z;

        // Compute determinants and generate improvements
        float d = determinant(mat3(rcol, scol, tcol));
        if (abs(d) < 1e-20) {
            return false; // Determinant too small, likely singular
        }
        // Newton Raphson update formula, x_new = x_old - f(x) / f'(x), but generalized to 
        // multiple dimensions. fcol has principle values, while other two contain gradients, 
        // so the ratio effectively calculates changes needed using Cramer's rule.
        rst.x = rst_curr.x - determinant(mat3(fcol, scol, tcol)) / d;
        rst.y = rst_curr.y - determinant(mat3(rcol, fcol, tcol)) / d;
        rst.z = rst_curr.z - determinant(mat3(rcol, scol, fcol)) / d;

        // Check for convergence
        if (all(lessThan(abs(rst - rst_curr), vec3(CONVERGED_ERROR))))
            converged = true;

        rst_curr = rst; // Commit the updated canonical coordinates
    }

    // Check for containment
    // (this test's a bit odd, but the linear pyramid's RST coords look similar to a box)
    if (any(lessThan(rst, vec3(0.0))) || any(greaterThan(rst, vec3(1.0)))) return false;

    // Interpolate the values stored on the element nodes
    value = e.v0.w * w0 + e.v1.w * w1 + e.v2.w * w2 + e.v3.w * w3 + e.v4.w * w4;
    return true;
}

//-----------------------------------------------------------------------------
// Utils
//-----------------------------------------------------------------------------

vec3 viridis(float t) {
    t = clamp(t, 0.0, 1.0);

    const vec3 c0 = vec3(0.2777273272234177, 0.005407344544966578, 0.3340998053353061);
    const vec3 c1 = vec3(0.1050930431085774, 1.404613529898575, 1.384590162594685);
    const vec3 c2 = vec3(-0.3308618287255563, 0.214847559468213, 0.09509516302823659);
    const vec3 c3 = vec3(-4.634230498983486, -5.799100973351585, -19.33244095627987);
    const vec3 c4 = vec3(6.228269936347081, 14.17993336680509, 56.69055260068105);
    const vec3 c5 = vec3(4.776384997670288, -13.74514537774601, -65.35303263337234);
    const vec3 c6 = vec3(-5.435455855934631, 4.645852612178535, 26.3124352495832);

    vec3 srgb = c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
    return pow(srgb, vec3(2.2));

}

vec4 over(vec4 a, vec4 b) {
  vec4 result;
  result.a = a.a + b.a * (1.f - a.a);
  if (result.a == 0.f)
    return a; // avoid NaN
  result.rgb = (a.rgb * a.a + b.rgb * b.a * (1.f - a.a)) / result.a;
  return result;
}

#define M_2PI 6.2831853071795865f
#define M_GRC 0.61803398875f

// Vertex positions for the element

//-----------------------------------------------------------------------------
// Main functions
//-----------------------------------------------------------------------------

Pyramid CreatePyramid() {
    Pyramid pyr;

    float time = iTime + 10.0;
    const float angle_offset = M_2PI / 4.0;

    // Base quad
    pyr.v0 = vec4(cos(1.0 * angle_offset), -0.75, sin(1.0 * angle_offset), .5 + .5 * cos(time*1.1));
    pyr.v1 = vec4(cos(2.0 * angle_offset), -0.75, sin(2.0 * angle_offset), .5 + .5 * cos(time*1.2));
    pyr.v2 = vec4(cos(3.0 * angle_offset), -0.75, sin(3.0 * angle_offset), .5 + .5 * cos(time*1.3));
    pyr.v3 = vec4(cos(4.0 * angle_offset), -0.75, sin(4.0 * angle_offset), .5 + .5 * cos(time*1.4));

    // Top point
    pyr.v4 = vec4( 0.0, .75, 0.0, .5 + .5 * cos(time*0.5));

    return pyr;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 v = -1.0 + 2.0*q;
    v.x *= iResolution.x/ iResolution.y;

    float an = 0.25*iTime + 6.283185*iMouse.x/iResolution.x;
    vec3 ro = vec3( 3.5*cos(an), 0.7, 3.5*sin(an) );
    vec3 ta = vec3( 0.0, -0.1, 0.0 );

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    // create view ray
    vec3 rd = normalize( v.x*uu + v.y*vv + 3.0*ww );

    // create element
    Pyramid pyr = CreatePyramid();

	vec4 color = vec4(.0);

    float startRayOffset = 0.0f;
    int frame = iFrame % 64;

    // blue noise
    startRayOffset = texture(iChannel0, fragCoord / 1024.0f).r;
    startRayOffset = fract(startRayOffset + float(frame) * M_GRC);


	float zMin         = 2.0;
    float zMax         = 5.0;
	float step         = (zMax - zMin) / float(NUM_STEPS);
    vec3 p             = ro + rd * zMin + rd * step * startRayOffset;    
	int zer = int(min(iTime, 0.)); // Hack to force loop unrolling.
    for(int i = zer; i < NUM_STEPS; i++)
	{
        float value; 
        if (interpolate(p, pyr, value)) {
            // Colormapped value and a hardcoded optical density
            vec3 rgb = viridis(value) * EXPOSURE;
            float density = 2.0;
            float rho = 1.0 - exp(-density * step);
            color = over(color, vec4(rgb, rho));
            if (color.a > .95) break;
        }
		p += rd*step;
	}

    // Composite over a background
    color = over(color, vec4(vec3(0.01)*(1.0-0.2*length(v)), 1.0));

    color.rgb = pow(color.rgb, vec3(1.0/2.2));

    // cheap dithering
    color.rgb += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

    fragColor = color;

}
