#if HW_PERFORMANCE==0
// lots of error, but plays well on mobile
#define NEWTON_ITERATIONS 2
#define NUM_STEPS 8
#else
#define NEWTON_ITERATIONS 10
#define NUM_STEPS 32    
#endif

#define CONVERGED_ERROR 1e-3
#define DIVERGED_ERROR 1e6

#define EXPOSURE 2.5

void pentagonalPrismInterpolationFunctions(vec3 rst, 
    out float w0, out float w1, out float w2, out float w3, out float w4, 
    out float w5, out float w6, out float w7, out float w8, out float w9) 
{
    // Compute iso-parametric interpolation functions
    // http://dilbert.engr.ucdavis.edu/~suku/nem/papers/polyelas.pdf

    // transforming parametric coordinates [0,1] to isoparametric [-1,1]
    float x = 2.0 * (rst.x - .5);
    float y = 2.0 * (rst.y - .5);
    float z = rst.z; // t is in [0,1]

    // From Appendix A.1 Pentagonal reference element (n = 5)
    float b = 87.05 - 12.7004 * x * x - 12.7004 * y * y;

    float a0 = -0.0929370 * (3.23607 + 4.0 * x) * (-3.80423 + 3.80423 * x - 2.76393 * y) * (15.2169 + 5.81234 * x + 17.8885 * y);
    float a1 = -0.0790569 * (3.80423 - 3.80423 * x - 2.76393 * y) * (-3.80423 + 3.80423 * x - 2.76393 * y) * (15.2169 + 5.81234 * x + 17.8885 * y);
    float a2 = -0.0790569 * (15.2169 + 5.81234 * x - 17.8885 * y) * (3.80423 - 3.80423 * x - 2.76393 * y) * (-3.80423 + 3.80423 * x - 2.76393 * y);
    float a3 = +0.0929370 * (3.23607 + 4.0 * x) * (15.2169 + 5.81234 * x - 17.8885 * y) * (3.80423 - 3.80423 * x - 2.76393 * y);
    float a4 = +0.0232343 * (3.23607 + 4.0 * x) * (15.2169 + 5.81234 * x - 17.8885 * y) * (15.2169 + 5.81234 * x + 17.8885 * y);

    w0 = -(a0 / b) * (z - 1.0);
    w5 = +(a0 / b) * (z - 0.0);

    w1 = -(a1 / b) * (z - 1.0);
    w6 = +(a1 / b) * (z - 0.0);

    w2 = -(a2 / b) * (z - 1.0);
    w7 = +(a2 / b) * (z - 0.0);

    w3 = -(a3 / b) * (z - 1.0);
    w8 = +(a3 / b) * (z - 0.0);

    w4 = -(a4 / b) * (z - 1.0);
    w9 = +(a4 / b) * (z - 0.0);
}

void pentagonalPrismInterpolationDerivs(vec3 rst, 
    out vec3 dw0, out vec3 dw1, out vec3 dw2, out vec3 dw3, out vec3 dw4, 
    out vec3 dw5, out vec3 dw6, out vec3 dw7, out vec3 dw8, out vec3 dw9)
{
  // Compute iso-parametric interpolation functions
  // http://dilbert.engr.ucdavis.edu/~suku/nem/papers/polyelas.pdf

  // transforming parametric coordinates [0,1] to isoparametric [-1,1]
  float x = 2.0 * (rst.x - .5);
  float y = 2.0 * (rst.y - .5);
  float z = rst.z; // t is in [0,1]

  vec3 dd0, dd1, dd2, dd3, dd4, dd5;

  // x-derivatives
  // First pentagon
  float x2 = x * x;
  float y2 = y * y;
  float denom = (-12.7004 * x2 - 12.7004 * y2 + 87.05);
  float denom2 = denom * denom;

  // Please excuse the line length. This code was generated using the
  // symbolic math package SymPy. (http://www.sympy.org)

  dd0.x = 25.4008 * x * (-0.371748 * x - 0.30075063759) * (3.80423 * x - 2.76393 * y - 3.80423) *
      (5.81234 * x + 17.8885 * y + 15.2169) / denom2 +
    5.81234 * (-0.371748 * x - 0.30075063759) * (3.80423 * x - 2.76393 * y - 3.80423) / denom +
    3.80423 * (-0.371748 * x - 0.30075063759) * (5.81234 * x + 17.8885 * y + 15.2169) / denom -
    0.371748 * (3.80423 * x - 2.76393 * y - 3.80423) * (5.81234 * x + 17.8885 * y + 15.2169) /
      denom;

  dd1.x = 25.4008 * x * (0.300750630687 * x + 0.218507737617 * y - 0.300750630687) *
      (3.80423 * x - 2.76393 * y - 3.80423) * (5.81234 * x + 17.8885 * y + 15.2169) / denom2 +
    5.81234 * (0.300750630687 * x + 0.218507737617 * y - 0.300750630687) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom +
    3.80423 * (0.300750630687 * x + 0.218507737617 * y - 0.300750630687) *
      (5.81234 * x + 17.8885 * y + 15.2169) / denom +
    0.300750630687 * (3.80423 * x - 2.76393 * y - 3.80423) * (5.81234 * x + 17.8885 * y + 15.2169) /
      denom;

  dd2.x = 25.4008 * x * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (-0.459505582146 * x + 1.41420935565 * y - 1.20300094161) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom2 +
    3.80423 * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (-0.459505582146 * x + 1.41420935565 * y - 1.20300094161) / denom -
    0.459505582146 * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom -
    3.80423 * (-0.459505582146 * x + 1.41420935565 * y - 1.20300094161) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom;

  dd3.x = 25.4008 * x * (0.371748 * x + 0.30075063759) * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (5.81234 * x - 17.8885 * y + 15.2169) / denom2 +
    5.81234 * (0.371748 * x + 0.30075063759) * (-3.80423 * x - 2.76393 * y + 3.80423) / denom -
    3.80423 * (0.371748 * x + 0.30075063759) * (5.81234 * x - 17.8885 * y + 15.2169) / denom +
    0.371748 * (-3.80423 * x - 2.76393 * y + 3.80423) * (5.81234 * x - 17.8885 * y + 15.2169) /
      denom;

  dd4.x = 25.4008 * x * (0.0929372 * x + 0.075187821201) * (5.81234 * x - 17.8885 * y + 15.2169) *
      (5.81234 * x + 17.8885 * y + 15.2169) / denom2 +
    5.81234 * (0.0929372 * x + 0.075187821201) * (5.81234 * x - 17.8885 * y + 15.2169) / denom +
    5.81234 * (0.0929372 * x + 0.075187821201) * (5.81234 * x + 17.8885 * y + 15.2169) / denom +
    0.0929372 * (5.81234 * x - 17.8885 * y + 15.2169) * (5.81234 * x + 17.8885 * y + 15.2169) /
      denom;

  // y-derivatives
  // First pentagon
  dd0.y = 25.4008 * y * (-0.371748 * x - 0.30075063759) * (3.80423 * x - 2.76393 * y - 3.80423) *
      (5.81234 * x + 17.8885 * y + 15.2169) / denom2 +
    17.8885 * (-0.371748 * x - 0.30075063759) * (3.80423 * x - 2.76393 * y - 3.80423) / denom -
    2.76393 * (-0.371748 * x - 0.30075063759) * (5.81234 * x + 17.8885 * y + 15.2169) / denom;

  dd1.y = 25.4008 * y * (0.300750630687 * x + 0.218507737617 * y - 0.300750630687) *
      (3.80423 * x - 2.76393 * y - 3.80423) * (5.81234 * x + 17.8885 * y + 15.2169) / denom2 +
    17.8885 * (0.300750630687 * x + 0.218507737617 * y - 0.300750630687) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom -
    2.76393 * (0.300750630687 * x + 0.218507737617 * y - 0.300750630687) *
      (5.81234 * x + 17.8885 * y + 15.2169) / denom +
    0.218507737617 * (3.80423 * x - 2.76393 * y - 3.80423) * (5.81234 * x + 17.8885 * y + 15.2169) /
      denom;

  dd2.y = 25.4008 * y * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (-0.459505582146 * x + 1.41420935565 * y - 1.20300094161) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom2 -
    2.76393 * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (-0.459505582146 * x + 1.41420935565 * y - 1.20300094161) / denom +
    1.41420935565 * (-3.80423 * x - 2.76393 * y + 3.80423) * (3.80423 * x - 2.76393 * y - 3.80423) /
      denom -
    2.76393 * (-0.459505582146 * x + 1.41420935565 * y - 1.20300094161) *
      (3.80423 * x - 2.76393 * y - 3.80423) / denom;

  dd3.y = 25.4008 * y * (0.371748 * x + 0.30075063759) * (-3.80423 * x - 2.76393 * y + 3.80423) *
      (5.81234 * x - 17.8885 * y + 15.2169) / denom2 -
    17.8885 * (0.371748 * x + 0.30075063759) * (-3.80423 * x - 2.76393 * y + 3.80423) / denom -
    2.76393 * (0.371748 * x + 0.30075063759) * (5.81234 * x - 17.8885 * y + 15.2169) / denom;

  dd4.y = 25.4008 * y * (0.0929372 * x + 0.075187821201) * (5.81234 * x - 17.8885 * y + 15.2169) *
      (5.81234 * x + 17.8885 * y + 15.2169) / denom2 +
    17.8885 * (0.0929372 * x + 0.075187821201) * (5.81234 * x - 17.8885 * y + 15.2169) / denom -
    17.8885 * (0.0929372 * x + 0.075187821201) * (5.81234 * x + 17.8885 * y + 15.2169) / denom;

  // z-derivatives
  // First pentagon
  float b = 87.05 - 12.7004 * x * x - 12.7004 * y * y;
  dd0.z = -0.092937 * (3.23607 + 4. * x) * (-3.80423 + 3.80423 * x - 2.76393 * y) *
    (15.2169 + 5.81234 * x + 17.8885 * y) / b;
  dd1.z = -0.0790569 * (3.80423 - 3.80423 * x - 2.76393 * y) *
    (-3.80423 + 3.80423 * x - 2.76393 * y) * (15.2169 + 5.81234 * x + 17.8885 * y) / b;
  dd2.z = -0.0790569 * (15.2169 + 5.81234 * x - 17.8885 * y) *
    (3.80423 - 3.80423 * x - 2.76393 * y) * (-3.80423 + 3.80423 * x - 2.76393 * y) / b;
  dd3.z = 0.092937 * (3.23607 + 4.0 * x) * (15.2169 + 5.81234 * x - 17.8885 * y) *
    (3.80423 - 3.80423 * x - 2.76393 * y) / b;
  dd4.z = 0.0232343 * (3.23607 + 4.0 * x) * (15.2169 + 5.81234 * x - 17.8885 * y) *
    (15.2169 + 5.81234 * x + 17.8885 * y) / b;

  // Note, multiplying by 2 to account for [-1; 1] to [ 0; 1] range
  dw0.x = 2.0 * (-dd0.x * (z - 1.0));
  dw5.x = 2.0 * (+dd0.x * (z + 0.0));
  dw0.y = 2.0 * (-dd0.y * (z - 1.0));
  dw5.y = 2.0 * (+dd0.y * (z + 0.0));
  dw0.z = 2.0 * (-dd0.z);            
  dw5.z = 2.0 * (+dd0.z);            

  dw1.x = 2.0 * (-dd1.x * (z - 1.0));
  dw6.x = 2.0 * (+dd1.x * (z + 0.0));
  dw1.y = 2.0 * (-dd1.y * (z - 1.0));
  dw6.y = 2.0 * (+dd1.y * (z + 0.0));
  dw1.z = 2.0 * (-dd1.z);            
  dw6.z = 2.0 * (+dd1.z);            

  dw2.x = 2.0 * (-dd2.x * (z - 1.0));
  dw7.x = 2.0 * (+dd2.x * (z + 0.0));
  dw2.y = 2.0 * (-dd2.y * (z - 1.0));
  dw7.y = 2.0 * (+dd2.y * (z + 0.0));
  dw2.z = 2.0 * (-dd2.z);            
  dw7.z = 2.0 * (+dd2.z);            

  dw3.x = 2.0 * (-dd3.x * (z - 1.0));
  dw8.x = 2.0 * (+dd3.x * (z + 0.0));
  dw3.y = 2.0 * (-dd3.y * (z - 1.0));
  dw8.y = 2.0 * (+dd3.y * (z + 0.0));
  dw3.z = 2.0 * (-dd3.z);            
  dw8.z = 2.0 * (+dd3.z);            

  dw4.x = 2.0 * (-dd4.x * (z - 1.0));
  dw9.x = 2.0 * (+dd4.x * (z + 0.0));
  dw4.y = 2.0 * (-dd4.y * (z - 1.0));
  dw9.y = 2.0 * (+dd4.y * (z + 0.0));
  dw4.z = 2.0 * (-dd4.z);            
  dw9.z = 2.0 * (+dd4.z);            
}

bool interpolatePentagonalPrism(vec3 P, 
    inout vec4 V0, inout vec4 V1, inout vec4 V2, inout vec4 V3, inout vec4 V4, 
    inout vec4 V5, inout vec4 V6, inout vec4 V7, inout vec4 V8, inout vec4 V9) {

    // Initialize our canonical coordinates to be in the center of the element
    // We'll iteratively refine these with Newton's method to match the given "P"
    vec3 pc_curr = vec3(0.5, 0.5, 0.5);
    vec3 pc = vec3(0.5, 0.5, 0.5);

    // Iteration for Newton's method
    bool converged = false;
    for (int iteration = 0; iteration < NEWTON_ITERATIONS; iteration++) {
        // Compute the currently optimized position in the canonical element
        float sf0, sf1, sf2, sf3, sf4, sf5, sf6, sf7, sf8, sf9;
        pentagonalPrismInterpolationFunctions(pc_curr, sf0, sf1, sf2, sf3, sf4, sf5, sf6, sf7, sf8, sf9);
        vec3 fcol = V0.xyz * sf0 + V1.xyz * sf1 + V2.xyz * sf2 + V3.xyz * sf3 + V4.xyz * sf4 
                  + V5.xyz * sf5 + V6.xyz * sf6 + V7.xyz * sf7 + V8.xyz * sf8 + V9.xyz * sf9;
        fcol -= P; // Making P be the origin

        // Now compute Jacobian, which transforms change in pc to change in shape functions
        vec3 dV0, dV1, dV2, dV3, dV4, dV5, dV6, dV7, dV8, dV9;
        pentagonalPrismInterpolationDerivs(pc_curr, dV0, dV1, dV2, dV3, dV4, dV5, dV6, dV7, dV8, dV9);

        // Now get derivatives in world space
        vec3 rcol = V0.xyz * dV0.x + V1.xyz * dV1.x + V2.xyz * dV2.x + V3.xyz * dV3.x + V4.xyz * dV4.x 
                  + V5.xyz * dV5.x + V6.xyz * dV6.x + V7.xyz * dV7.x + V8.xyz * dV8.x + V9.xyz * dV9.x;
        vec3 scol = V0.xyz * dV0.y + V1.xyz * dV1.y + V2.xyz * dV2.y + V3.xyz * dV3.y + V4.xyz * dV4.y 
                  + V5.xyz * dV5.y + V6.xyz * dV6.y + V7.xyz * dV7.y + V8.xyz * dV8.y + V9.xyz * dV9.y;
        vec3 tcol = V0.xyz * dV0.z + V1.xyz * dV1.z + V2.xyz * dV2.z + V3.xyz * dV3.z + V4.xyz * dV4.z 
                  + V5.xyz * dV5.z + V6.xyz * dV6.z + V7.xyz * dV7.z + V8.xyz * dV8.z + V9.xyz * dV9.z;

        // The above should be our 3X3 jacobian.

        // Compute determinants and generate improvements
        float d = determinant(mat3(rcol, scol, tcol));
        if (abs(d) < 1e-20) {
            return false; // Determinant too small, likely singular
        }
        // Newton Raphson update formula, x_new = x_old - f(x) / f'(x), but generalized to 
        // multiple dimensions. fcol has principle values, while other two contain gradients, 
        // so the ratio effectively calculates changes needed using Cramer's rule.
        pc.x = pc_curr.x - (determinant(mat3(fcol, scol, tcol)) / d);
        pc.y = pc_curr.y - (determinant(mat3(rcol, fcol, tcol)) / d);
        pc.z = pc_curr.z - (determinant(mat3(rcol, scol, fcol)) / d);


        if (any(greaterThan(abs(pc), vec3(DIVERGED_ERROR))))
            return false;

        vec3 err = abs(pc - pc_curr);
        pc_curr = pc; // Commit the updated canonical coordinates

        // Check for convergence
        if (all(lessThan(err, vec3(CONVERGED_ERROR)))) break;
    }

    pentagonalPrismInterpolationFunctions(pc_curr, V0.w, V1.w, V2.w, V3.w, V4.w, V5.w, V6.w, V7.w, V8.w, V9.w);   

    // Check for containment
    if (any(lessThan(vec4(V0.w, V1.w, V2.w, V3.w), vec4(0.0)))) return false;
    if (any(lessThan(vec4(V4.w, V5.w, V6.w, V7.w), vec4(0.0)))) return false;
    if (any(lessThan(vec2(V8.w, V9.w), vec2(0.0)))) return false;

    if (any(greaterThan(vec4(V0.w, V1.w, V2.w, V3.w), vec4(1.0)))) return false;
    if (any(greaterThan(vec4(V4.w, V5.w, V6.w, V7.w), vec4(1.0)))) return false;
    if (any(greaterThan(vec2(V8.w, V9.w), vec2(1.0)))) return false;


    float sum = V0.w + V1.w + V2.w + V3.w + V4.w + V5.w + V6.w + V7.w + V8.w + V9.w;

    if (abs(1.0 - sum) > 1e-4f) return false;

    return true;
}


