const int REMOVE = 0;

// custom images added following https://www.shadertoy.com/view/lfGXzV
// Better weight map textures after running command from here in console (ctrl+shift+j) :
// github.com/patuwwy/ShaderToy-Chrome-Plugin/issues/181 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Sample the image 
    // (CHANGE IMAGE HERE!)
    vec4 color = texture( iChannel0, uv);

    // perform bit removal
    float factor = 255.0;
    int remove_num = 256 - int(pow(2.0, float(REMOVE)));
    color.r = float(int(color.r * factor) & remove_num) / factor;
    color.g = float(int(color.g * factor) & remove_num) / factor;
    color.b = float(int(color.b * factor) & remove_num) / factor;

    float r = color.r * 0.3 + color.g + 0.6 + color.b * 0.1;
    float g = color.r * 0.3 + color.g + 0.6 + color.b * 0.1;
    float b = color.r * 0.3 + color.g + 0.6 + color.b * 0.1;


    fragColor = color;

    // GrayScale:
    // fragColor = vec4(r, g, b, 1.0);
}
