void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 l = normalize(vec3(.5,1.,.05));
    vec3 a = normalize(vec3(-texture(iChannel0, (fragCoord) / iResolution.xy+vec2(.00001,0.)).x+texture(iChannel0, (fragCoord) / iResolution.xy).x,
                    -texture(iChannel0, (fragCoord) / iResolution.xy+vec2(0.,.00001)).x+texture(iChannel0, (fragCoord) / iResolution.xy).x,.000005));
    fragColor = vec4(0.2*dot(a,l),0.6*dot(a,l),1.5*dot(a,l),0.)+vec4(0.2,0.2,0.2,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dMouse = length(iMouse.xy - fragCoord) / iResolution.x;
    dMouse = dMouse * dMouse;
    float d = max(texture(iChannel0, fragCoord / iResolution.xy).x - .001, sqrt(.005 - dMouse));
    fragColor = vec4(max(0.,d),0.,0.,0.);
}
