const float PI = acos(-1.0);

vec3 alt(vec2 v, float time){

    float c = 0.3*cos(smoothstep(-0.5, 0.5, sin(v.y*8.0))) + sin(4.0*v.x) - 0.2;
    return vec3(sin(v.y + smoothstep(-0.5, 0.5, fract(c)) + time*5.0), 0.0, 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec2 m;
    m.x = atan(uv.x / uv.y) / PI;
	m.y = 1.0 / sqrt(length(uv)) *0.8;
	float d = m.y;

    m.x -= iTime * 0.1; //sin( iTime ) * 0.1;
	m.y += iTime * 0.25;

    vec3 t = alt( m * PI, iTime ) / d;

    // Output to screen
    fragColor = vec4(t,1.0);
}
