// This function let's us jump from 2D-UV to spherical 3D-XYZ position
vec3 uvTo3D(vec2 uv) {
    // The 2-Sphere point via UV => Lat/Long angles
    float theta = uv.x * 2.0 * 3.14159265359; // Longitude
    float phi = uv.y * 3.14159265359; // Latitude
    // Trigonometry from 2-Sphere angles to an XYZ normalized [-1,1].
    float x = sin(phi) * cos(theta);
    float y = sin(phi) * sin(theta);
    float z = cos(phi);
    // { Dev Note }
    // If you're porting this shader to a material, I strongly recommend you skip this function and just use the XYZ of your `varying vNormal` in place of the result you would get here. Should be suitable for all spheres and most round geometries
    return vec3(x, y, z);
}

float sdfLine(vec3 p) {
    float radius = .002;
    //p*=.5;
    float closestXLine = length(vec2(round(p.yz))-p.yz)-radius;
    float closestYLine = length(vec2(round(p.xz))-p.xz)-radius;
    float closestZLine = length(vec2(round(p.xy))-p.xy)-radius;
    return min(closestXLine,min(closestYLine,closestZLine));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor= vec4(0.);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 pos = uvTo3D(uv); // UV => 3D for equirectangular/spherical mapping

    // Simple raymarching blog reference: https://michaelwalczyk.com/blog-ray-marching.html
    const float MINIMUM_HIT_DISTANCE = 0.02;
    const int NUMBER_OF_STEPS = 25;
    float total_distance_traveled = 0.;
    //fragColor=(vec4(sdfLine(pos)));
    for (int i = 0; i < NUMBER_OF_STEPS; ++i){
        vec3 current_position = pos/1.2+iTime + total_distance_traveled * pos;
        float distance_to_closest = sdfLine(current_position);
        if (distance_to_closest < MINIMUM_HIT_DISTANCE) // hit
        {
            fragColor=vec4(vec3(1.),1.);
            break;
        }
        total_distance_traveled += distance_to_closest;
        //if(total_distance_traveled>2.){break;}
    }
}

