#define at sin((atan(uv.x,uv.y)*3.)+length(uv)*28.
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
   float d1,d2,t = iTime/6.;
   uv *= rot(t)*.3;
   d1 = smoothstep(-1.,1.,at-fract(length(uv*22.)-t)/2.5)*7.);
   d2 = at+pow(fract(length(uv*22.)-t),7.))*6.;
   float r = (d1*d2-d2/1.4-d1/2.)*.23;
   fragColor = vec4(r);
}
