void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv0 = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    float som = textureLod( iChannel0, vec2(length(uv0), 0.75), 0.0 ).x;
    float l = pow(length(uv0), som);
    uv0 *= l;

    vec2 uv = uv0;


    vec3 final = vec3(0.);

    for (float i = 0.; i < 4.; i++) {
        uv = fract(uv) * 1.5 - 0.75;

        vec3 d = vec3(length(uv) - l);

        vec3 cor = (cos(iTime + i + vec3(1., 0.5, 3.) + 5.*l) / 2. + 1.);

        // Time varying pixel color
        vec3 col = cos(10. * d + 3. * iTime) / 2. + 0.5;
        col = 0.02 / col;

        final += col * cor * exp(-i);
    }

    // Output to screen
    fragColor = vec4(final, 1.0);
}
