void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = 10.0+mod(iTime*5.0,100.0);
    vec3 col = texture(iChannel0,uv).xyz;

    //this is unecessary but I'm porting this to a game and it only supplies uv coords
    vec2 fragCoordinate = uv*iResolution.xy;

    float seed = (fragCoordinate.y * iResolution.x + fragCoordinate.x);

    float delta = fract(t*sin(seed*0.1084137)*1.3214519);

    float r = fract(t*sin(seed*0.2313652)*1.2492199);

    float g = fract(t*cos(seed*0.3414321)*1.4238714);

    float b = fract(t*sin(seed*0.1311449)*1.1249482);

    fragColor = vec4(col+ delta * 0.2 * vec3(r,g,b),1.0);
}
