float m = 4.0, n=5.0, th=0.01;

vec3 func(vec2 uv) {
    vec3 col;
    float df = 0.2*iTime;
    float y = cos(n/m * asin(uv.x)+df);
    col.x = step(distance(vec2(uv.x, y), uv), th);//float(abs(uv.y - y) < 0.05);
    for (float k=1.; k<m; k=k+1.) {
        y = cos(n/m * (asin(uv.x) + k*3.1415*2.)+df);
        col.x += step(distance(vec2(uv.x,y), uv), th)/(1.);//#float(abs(uv.xy - y) < 0.05); 
    }
    y = cos(n/m * (3.1415 - asin(uv.x))+df);
    col.y += step(distance(vec2(uv.x,y), uv), th);//float(abs(uv.y - y) < 0.05);
    for (float k=1.; k<m; k=k+1.) {
        y = cos(n/m * (3.1415 - asin(uv.x) + k*3.1415*2.)+df);
        col.y += step(distance(vec2(uv.x,y), uv), th)/(1.);//float(abs(uv.y - y) < 0.05); 
    }
    return col;
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv *= 4.0;

    fragColor = vec4(func(vec2(uv.x, uv.y)).xy, 0.0, 1.0);
}
