void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    a = sin(sin(iTime/5.)+iTime/20.);
    w = int(iResolution.x);
    h = int(iResolution.y);
    t = w/2+int(fragCoord.x+fragCoord.y*iResolution.x);
    z = (iResolution.y/2.-fragCoord.y)/(sin(iTime/3.)*20.+3.);

    x = sin(a     )*float((t  %w)-w/2)/z +
        cos(a     )*float((t/w%w)+500)/z ;

    y = cos(a     )*float((t  %w)-w/2)/z +
        sin(a+3.14)*float((t/w%w)+500)/z +iTime*18.; 

     col = float((
                    i[(int(y)&31)]<<(int(x)&31)>>31
                    )&1);

    // Output to screen
    fragColor = vec4(0,col*((0.7+z)/25.),0.,0.);
}
