float rand(in float seed, in float minn, in float maxx)
{
    return fract(sin(seed * 614.357) * 865.39) * (maxx - minn) + minn;
}
mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

//  Function from IÃ±igo Quiles
//  ref: https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	vec2 uv = (2.0*fragCoord-iResolution.xy) / iResolution.y;
    uv *= rotate2d(iTime / 2.0);
    uv = abs(uv);
    uv = vec2(min(uv.x,uv.y), max(uv.x,uv.y));
    uv = mod(uv - fract(iTime / 2.0) + 0.5, 1.0);
    uv /= (iTime / 2.0);

    uv *= rotate2d(iTime);

    vec4 image = texture(iChannel0, uv * iTime);

    vec4 noise = texture(iChannel1, uv * iTime);
    vec3 colorA = hsb2rgb(vec3(sin(length(uv * iTime *3.0)),1.0,1.0));
    vec3 color = mix(colorA, image.rgb, 1.0 / length(uv * iTime));

    // Output to screen
    fragColor = vec4(image.xyz * color.xyz, 1.0);
    //fragColor = vec4(image.xyz, 1.0);
}
