// https://www.shadertoy.com/view/lltSRB
// Tessellated Mandelbrot
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

#define PI 3.14526
#define PI2 (PI * 2.0)

vec4 texcat(vec2 uv) {
    uv.x = -0.01 + mod(uv.x, 0.99) / 6.0 + 0.94 * mod(floor(iTime * 6.0), 6.0) / 6.0 ;
    return texture(iChannel0, uv);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    float asp = iResolution.x / iResolution.y;
    vec2 pos = uv - 0.5;
    pos.y /= asp;
	// based on IQ's https://www.shadertoy.com/view/lllGWH
    vec2 c = vec2(-.744, .18631) + pos * pow(10.0, 3.0 * cos(.15 * iTime) - 2.0);
    // 
    vec2 z = vec2(0.);

    int i = 0;

    for (; i < 60; i++) {
        z = mat2(z, -z.y, z.x) * z + c;
        float r = dot(z, z);
        if (r > 1e4) {
            vec2 fuv = vec2((atan(z.y, z.x) / PI2 - iTime), -0.1 + log(log(r)));
            fragColor = texcat(fuv);
            return;
        }
    }
    fragColor = texcat((z * 10.0 + (-2.0 * iTime)) + 0.5);
}
