void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centeredUV = (fragCoord-iResolution.xy*0.5)/iResolution.x;

    float fov = sin(iTime*0.5)*0.1 + 0.1;

    vec3 viewDir = normalize(vec3(centeredUV, fov));

    float scale = 1./fov;

    fragColor = texture(iChannel0, viewDir.xy/scale*4.).bgra;

    //fragColor = vec4(viewDir, 1.0);
}
