vec2 grad(vec2 uv) {
    // Compute the gradient of the. channel used for shading
    vec3 delta = 1.0/iResolution;

    vec2 delta_x = vec2(1.0, 0.0)/iResolution.xy;
    vec2 delta_y = vec2(0.0, 1.0)/iResolution.xy;

    vec4 nextTime_x = texture(iChannel0, uv+delta_x);
    vec4 nextTime_y = texture(iChannel0, uv + delta_y);
    vec4 prevTime_x = texture(iChannel0, uv-delta_x);
    vec4 prevTime_y = texture(iChannel0, uv-delta_y);


    float nextHeight_x = nextTime_x.r;
    float nextHeight_y = nextTime_y.r;
    float prevHeight_x = prevTime_x.r;
    float prevHeight_y = prevTime_y.r;

    vec2 result = (1.0/2.0) *vec2(nextHeight_x-prevHeight_x,nextHeight_y-prevHeight_y);

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 g = grad(uv);
    vec3 t1 = vec3(1,0,20.0*g.x);
    vec3 t2 = vec3(0,1,20.0*g.y);
    vec3 N = normalize(cross(t1,t2));
    vec3 L = normalize(vec3(1,3,5));
    float illum = (0.4+0.4*smoothstep(0.97,0.98, dot(N,L)) + 0.2 * max(0.0, dot(N,L)));

    float substance = 2.0*texture(iChannel0, uv).g;
    // Output to screen
    fragColor = illum*vec4(0.3, 0.4, 0.7,0.0) + (1.0-substance)*vec4(0.2,0.45,0.4, 0.0);
}

vec4 lap(vec2 uv) {
    // Compute the Laplacian of all four elements of iChannel0

    vec3 delta = 1.0/iResolution;
    vec4 currTime = texture(iChannel0, uv); //Current height

    vec2 delta_x = vec2(1.0, 0.0)/iResolution.xy;
    vec2 delta_y = vec2(0.0, 1.0)/iResolution.xy;

    vec4 nextTime_x = texture(iChannel0, uv+delta_x);
    vec4 nextTime_y = texture(iChannel0, uv + delta_y);
    vec4 prevTime_x = texture(iChannel0, uv-delta_x);
    vec4 prevTime_y = texture(iChannel0, uv-delta_y);

    float lapHeight = 1.0/4.0*(nextTime_x.r + prevTime_x.r + nextTime_y.r + prevTime_y.r-4.0*currTime.r);


    return vec4(lapHeight,0,0,0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 BufferA = texture(iChannel0, uv);

    // Suggested time step
    float dt = 0.5;

   /* Wave simulation constants */
    float d = 0.999;
    float c = 1.4;

    // INSERT CODE TO UPDATE A and B INSTEAD OF THE LINES BELOW ---> 

    float speed =  dt*d*(c*c)*lap(uv).x + BufferA.g*d;
    float height =  speed*dt + BufferA.r;
    // <----------

    vec2 V = fragCoord - iMouse.zw;
    if (iTime < 1.0)
        fragColor = vec4(0.0,0,0,0);
    else
        fragColor = exp(-0.05*dot(V,V))*vec4(0,1.0,0,0) + vec4(height, speed, 0.0, 0.0);
}
