#ifdef GL_FRAGMENT_PRECISION_HIGH
precision highp float;
#else
precision mediump float;
#endif

vec2 rotateCW(vec2 p, float a)
{
  mat2 m = mat2(cos(a), -sin(a), sin(a), cos(a));
  return p * m;
}

vec3 palette(float t)
{
  // blue & white
  //vec3 a = vec3(0.1, 0.31, 0.1);
  //vec3 b = vec3(0., 0.05, 0.9);
  //vec3 c = vec3(1.0, 1.0, 1.0);
  //vec3 d = vec3(0.263, 0.416, 0.557);

  //yellow white and pink
  //vec3 a = vec3(0.608, 0.500, 0.500);
  //vec3 b = vec3(0.588, 0.5, 0.5);
  //vec3 c = vec3(1.058, -0.672, -1.282);
  //vec3 d = vec3(0.0, 0.333, 0.667);

  //yellow purple magenta
  vec3 a = vec3(0.731, 1.098, 0.192);
  vec3 b = vec3(0.358, 1.090, 0.657);
  vec3 c = vec3(1.077, 0.360, 0.328);
  vec3 d = vec3(0.965, 2.265, 0.837);

  return a + b * cos(6.28318*(c*t*d));
}


void mainImage_orig(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
  vec2 uv0 = uv;
  vec3 finalColor = vec3(0.0);

  for (float i = 0.0; i < 5.0; i++) {
  uv = fract(uv * 2. ) - 0.5;
  uv = rotateCW(uv, i*cos(iTime*0.2)) ;

  float d = length(uv) * exp(-length(uv0));

  vec3 color = palette(d * 4. * cos(iTime));

  d = sin(d*4.0 + cos(0.25*iTime*(0.3-i)))/4.0;
  d = abs(d);
  d = 0.01/d;
  d = pow(d, 1.4);

  finalColor += color * d;
  }
  fragColor = vec4(finalColor, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 tcol=vec4(0.);
    const int AA=4;
    for( int mx=0; mx<AA; mx++ )
    for( int nx=0; nx<AA; nx++ )
    {
        vec2 o = vec2(float(mx),float(nx)) / float(AA) - 0.5;
        mainImage_orig(fragColor,fragCoord+o);
        tcol+=clamp(fragColor,0.,1.);
    }
    fragColor=tcol/float(AA*AA);
}
