const vec3 BG_COLOR = vec3(0.5, 0.8, 1.0); //Sky Background
const vec3 CIRCLE_COLOR = vec3(1.0, 0.8, 0.0); //Yellow Color
const vec3 MOUNT_COLOR = vec3(0.0, 0.6,0.0); //Mountain Color

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Sun Setup
    uv *= 2.0; // -1 to 1
    uv -= 1.0; // -1 to 1
    uv.x *= iResolution.x / iResolution.y; //Aspect Ratio
    uv = mod(uv, 0.1); // Increase the UV every 0.1 Units.

    //Create Circle
    //Circle Bob
    vec2 circleCenter = vec2(1.0, sin(iTime)) * 0.5; //Center

    float circleDist = distance(uv, circleCenter); //Center of circle and distance between UV

    float circleSmooth = smoothstep(0.48, 0.5, circleDist); //Avoid Artifacting.

    //vec3 background = BG_COLOR;
    //Put the sun in the back.
    vec3 circleColor = mix(CIRCLE_COLOR, BG_COLOR, circleSmooth);

    //Wave Setup
    uv = fragCoord / iResolution.xy; //Reset Coords
    float y = sin(uv.x * 8.0 + iTime) * 0.1 + 0.2;//Wave
    float d = step(y,uv.y);

    //Nyan Cat
    vec2 nyanUV = fragCoord.xy / iResolution.xy; //Reset Coord
    nyanUV.x -= iTime * 0.5; //Move
    nyanUV.y += sin(nyanUV.x * 8.0) * 0.1 + 0.2; //Wave

    vec4 nyanCatColor = texture(iChannel0, nyanUV * 5.0); // Sample Nyan Cat texture

    //Set Final Color
    vec3 finalColor = mix(MOUNT_COLOR, circleColor, d);

    //Add Nyan Cat
    fragColor = mix(vec4(finalColor, 1.0), nyanCatColor, nyanCatColor.a);

    // Output the final color
    fragColor.a = 1.0;
}
