#define t iTime - dot(iMouse,iMouse)/1e5
#define r mat2( cos(t),-sin(t),sin(t), cos(t))
#define R iResolution 

void mainImage( out vec4 o, vec2 i ){
    i = ( i - .5 * R.xy ) / R.y ;

    int j;
    vec2 p = i * r ;
    o-=o;
    while(j++ != 99)
        o[int(i = p - vec2(i.x * i.x - i.y * i.y, 2. * i.x * i.y) * 2.)]++;

    o.rg=1.-i;
}
