// Projecting into closest object iteratively, same
// as what's described here: https://en.wikipedia.org/wiki/Projections_onto_convex_sets#Related_algorithms

// Based on this: https://www.shadertoy.com/view/lXsSWX
    // Which is then based on this: https://www.shadertoy.com/view/slGyWt

// iquilezles.org/articles/distfunctions2d
float sdSegment( in vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a, ba = b-a;
    return length(pa-ba*clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0));
}

// .x = f(p), .yz = ∇f(p) with ‖∇f(p)‖ = 1, iquilezles.org/articles/distfunctions2d
vec3 sdgCircle( in vec2 p, in float r ) 
{
    float l = length(p);
    return vec3( l-r, p/l );
}

// .x = f(p), .yz = ∇f(p) with ‖∇f(p)‖ = 1, iquilezles.org/articles/distfunctions2d
vec3 sdgBox( in vec2 p, in vec2 b )
{
    vec2  w = abs(p)-b;
    vec2  s = vec2(p.x<0.0?-1:1,p.y<0.0?-1:1);
    float g = max(w.x,w.y);
	vec2  q = max(w,0.0);
    float l = length(q);
    return vec3(   (g>0.0)?l: g,
                s*((g>0.0)?q/l : ((w.x>w.y)?vec2(1,0):vec2(0,1))));
}

// .x = f(p), .yz = ∇f(p) with ‖∇f(p)‖ = 1, iquilezles.org/articles/distfunctions2d
// yes, I did duplicate this function without realising it was implemented above.
//
// The union between a segment and a circle is remarkably close! IQ's method of finding
// intersection points works well, the only issue is that I still don't know how to find
// an alternate surface to rely on when the component SDF and the intersection point
// are both unreliable.
//
// If the circle is in the direct centre of the segment, the SDF is exact excluding one point
// at the very centre of the circle.
vec3 sdgSegment( in vec2 p, in vec2 a, in vec2 b, in float r )
{
    vec2 ba = b-a, pa = p-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    vec2  q = pa-h*ba;
    float d = length(q);
    return vec3(d-r,q/d);
}

//---------------------------------------------------------------------

vec3 sdf1( in vec2 p ) { return sdgCircle(p-vec2(-0.5, -0.25),0.7); }
//vec3 sdf2( in vec2 p ) { return sdgCircle(p-vec2( 0.1,  0.25),0.8); }
vec3 sdf2( in vec2 p ) { return sdgBox(p-vec2( 0.1,  0.25), vec2(0.5,0.5)); }
//vec3 sdf2( in vec2 p ) { return sdgSegment(p,vec2(0.0,0.0),vec2(0.5,0.0),0.2); }

// This is my take on the issue of switching between the two shapes and their 
// intersecting corners. The idea is that if the nearest surface is inside another
// object, then it should not trust the SDF of the shape.

// There are still a couple of edge cases where this fails in every scenario.
// These issues are most prominent when the side of the box is tangent to the circle
// (change the x position of sdf1 to 0.0 and they'll appear)

// What this method does very well is demonstrate where we are to trust the naive
// union, and showing ther area in which we have to choose between the intersection
// points or a different piece of the border of a component shape.
float checkBoundary(vec2 p)
{
    vec3 a = sdf1(p);
    vec3 b = sdf2(p);

    // distances
    float ad = -a.x;
    float bd = -b.x;

    // normals
    vec2 an = a.yz;
    vec2 bn = b.yz;

    float probea = sdf1(p + bd * bn).x;
    float probeb = sdf2(p + ad * an).x;

    return -max(probea, probeb);
}

float opSubtract( in vec2 p )
{
    vec3  a = sdf1(p);
    vec3  b = sdf2(p);
    float d = min( a.x, b.x );

    if( d>0.0 ) return d;

    vec2 op = p;

    for( int i=0; i<32; i++ )
    {
        p -= 0.5*(a.x*a.yz + b.x*b.yz);
        a = sdf1(p);
        b = sdf2(p);
        if( max(abs(a.x),abs(b.x))<0.001 ) break;
    }

    float dc = length(p-op); //return -dc;

    float s = checkBoundary(op);
    //return s;
    //return d;
    return (s<0.0) ? d : -dc;
}

float naiveUnion(in vec2 p)
{
    return min(sdf1(p).x,sdf2(p).x);
}
// https://iquilezles.org/articles/sdfxor/
float xor(in vec2 p) {
    return max( min(sdf1(p),sdf2(p)), -max(sdf1(p),sdf2(p)) ).x;
}

float map( in vec2 p )
{
    //return xor(p);
    //return naiveUnion(p);
    return opSubtract(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2  m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    float px = 2.0/iResolution.y;

    if( iMouse.z<0.001 )
    {
        m = vec2(0.6,0.25)*sin(iTime*vec2(0.7,0.5)+vec2(0.0,2.0));
        m = vec2(m.x-m.y,m.x+m.y);
    }

    float d = map(p);

    // show SDF
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.5,0.85,1.0);
	col *= 1.0 - exp2(-32.0*abs(d));
	col *= 0.8 + 0.2*cos(128.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.002,0.005,abs(d)) );

    // show distance bound
    {
        float dm = map(m);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(dm))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

    // show search path to closest shape intersection/featurr
    if( d<0.0 )
    {
        vec2 w = m;
        float c = 1e20;
        for( int i = 0; i<16; i++ )
        {
            vec3 a = sdf1(w);
            vec3 b = sdf2(w);
            vec2 q = w - .5*(a.x*a.yz+b.x*b.yz);
            c = min( c, sdSegment(p,w,q)-0.005 );
            c = min( c, length(p-w)-0.015 );
            w = q;
        }
        col = mix(col, vec3(1.0,1.0,1.0), smoothstep(px, -px, c));
    }

    fragColor = vec4( col, 1.0 );
}
