// golfing 2051 chars "fractal Cloud" by eriben0628. https://shadertoy.com/view/NtKyzw

// -30 by FordPerfect, -10 by Fab 

float F(vec4 p) {
    vec4 z = p, k;
    for( float i; i < 1.; i += .067 ) {
        if( dot(z,z) > 4.)  return i;
        k = z;
        z = p + 2.*z.x * z; // quaternionMul(z,z) + p
        z.x -= dot(k,k);
    }
    return 9.;
}

