void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy;
    vec4 P = vec4(0, 0, -1.8, 0),                       // camera pos
        P2 = vec4(0, 5, 5.5, 0) + P,                    // light pos
         D = normalize( vec4(u+u - R, R.y, 0)), p2,L;
 // D.xz = ( D.xz - vec2(0,1) ) * mat2(cos(iTime+vec4(0,11,33,0))) + vec2(0,1); // camera rot
    float T = 1., I, r,r2,T2,
         di = .02, dj = .4, i,j;

    for( O *= 0.; i < 4.; i += di ) {
        L = normalize(P2-P);
        r = F(P),
        T *= exp(-di*r);
        if(r > .0001) {
            p2 = P; I = 0.; T2 = 1.;
            for( j=0.; j < 4.; j += dj ) {
                r2 = F(p2);
                r2 > .0001 ?                            // very nice without it
                    T2 *= exp(-dj*r2),
                    I += T2 / dot(p2-P2,p2-P2) : I;         
                p2 += dj * L;
            }                      
            O += di * T * vec4(2, 6.5, 5.8, 0)
                    * I * dj* mix( 1., abs(dot(L, D)) ,.5 );
          }                   // phase function
        P += di * D;
    }  
}
