float sphere(in vec2 p, in float r)
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x,iResolution.y);

    //DynamicRadius
    float Mask = (cos(uv.x*sin(iTime)*1.2687*2.0+iTime) * sin(uv.y*cos(iTime)*1.1428*2.0+iTime))/5.0;
    float r = sin(0.45);

    //DrawSphere
    float Circle = sphere(uv, r+Mask);
    Circle = pow(smoothstep(0.0, 0.5, Circle), 10.0);
    Circle = 0.1 / Circle;

    // Output to screen
    vec3 result = vec3(Circle);
    fragColor = vec4(result,1.0);
}
