const int n = 256;

int mandelbrot( vec2 c, vec2 j ) {
    vec2 z = c;
    for (int i = 0; i<n; i++) {
        if (length(z) > 2.) return i; z = vec2(z.x*z.x-z.y*z.y, 2.*z.x*z.y) + j;
    }
    return 0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    uv *= 2.;

    vec2 m = (iMouse.xy * 2. - iResolution.xy) / iResolution.y;
    m *= 2.;

    vec3 mcol;
    vec3 jcol;
    vec3 pcol;

    if (length(uv - m) < 0.05) {
        pcol = vec3(0,0,1);
    } else {
        mcol = vec3(mandelbrot(uv, uv)) / float(n) * 5.;
        jcol = vec3(mandelbrot(uv, m)) / float(n) * 5.;
    }

    fragColor = vec4(vec4(mcol, 1) + vec4(jcol, 0.5) + vec4(pcol, 1));
}
