const mat3 lms_from_lab = mat3(+4.077, -3.307, +0.231,
                       -1.268, +2.609, -0.341,
                       -0.004, -0.703,  1.707);
const mat3 rgb_from_lms = mat3(1.0, 1.0, 1.0,
                       0.3963377774, -0.1055613458, -0.0894842,
                       0.2158037573, -0.0638541728, -1.2914855480);


vec3 lab2srgb (vec3 lab) {
    //diagonalize rgb_from_lms... for research
    const mat3 S = mat3(-0.111329, +0.705205, +2.80479,
                    +0.133807, -2.90451, +1.89072,
                    +1.0, +1.0, +1.0);
    const mat3 J = mat3(-1.41479, 0.,0.,
                        0., -0.326373, 0.,
                        0., 0., +1.34411);
    const mat3 St = mat3(-0.465797, +0.203949, +0.920853,
                     +0.170663, -0.283265, +0.0569024,
                     +0.295135, +0.0793161, +0.0222441);
    //SJS = rgb_from_lms
    mat3 SJS = St*J*S;
    vec3 lms = lab*SJS;
    lms = lms*lms*lms;
    vec3 rgb = lms*lms_from_lab;
    return rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv2 = vec2(-0.5) + uv.xy;
    float L = 0.6+0.2*sin(iTime);
    float chroma = 0.2;
    vec3 lab = vec3(L, chroma*uv2.x, chroma*uv2.y);
    vec3 rgb = lab2srgb(lab);

    rgb = pow(rgb, vec3(1.0/2.2));
    fragColor = vec4(rgb,1.0);
}
