void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float col1 = texture(iChannel0,uv).a;
    float ref = 1.0-texture(iChannel1,uv).r;
    // Output to screen
    vec3 outputs = (abs(vec3((col1+ref)/2.0)-0.5)*30.0);
    fragColor = vec4(mix(outputs,texture(iChannel1,uv).rgb,0.1),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel1,uv).rgb;
    vec3 v = texture(iChannel0,uv).rgb;
    // Output to screen
    fragColor.r=col.r;
    fragColor.g=v.r;
    fragColor.b=v.g;
    fragColor.a=v.b;
}

