// ASC CDL color grading
// https://en.wikipedia.org/wiki/ASC_CDL
vec3 asc_cdl(vec3 col, vec3 slope, vec3 offset, vec3 power)
{
    return pow(max(col, 0.) * slope + offset, power);
}

vec3 view_transform(vec3 col)
{
    // avoid any negative values before using power functions
    col = max(col, 0.);

    // color grading
    col = asc_cdl(
        col,
        1.1 * vec3(1, .95, .94),
        vec3(0, 0, .007),
        vec3(1.04, 1, 1)
    );

    // flim
    const float exposure = 0.;
    col = flim_transform(col, exposure, false);

    // OETF (Linear BT.709 I-D65 to sRGB 2.2)
    col = pow(col, vec3(1. / 2.2));
    col = clamp(col, 0., 1.);

    return col;
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // bottom left pixel (0, 0) in the buffer stores the
    // resolution.
    if (ivec2(frag_coord) == ivec2(0))
    {
        frag_col = vec4(0, 0, 0, 1);
        return;
    }

    // sample buffer
    vec4 data = texelFetch(iChannel0, ivec2(frag_coord), 0);
    vec3 col = data.xyz / max(data.w, 1.);

    // view transform
    col = view_transform(col);

    // output
    frag_col = vec4(col, 1);
}

float bar_brightness(float u)
{
    return pow(remap01(u, -1.4, 1.), 6.) * 140. * step(u, 1.);
}

// render the bars (original scene without halation)
vec3 render(vec2 coord)
{
    vec2 uv = screen_to_uv_fit(coord, iResolution.xy);
    if (abs(uv.y - .2) < .1)
    {
        return vec3(bar_brightness(uv.x));
    }
    else if (abs(uv.y + .2) < .1)
    {
        return vec3(bar_brightness(-uv.x));
    }
    return vec3(0);
}

// render the original scene with halation.
// effectively just blur "the deep orange channel" and mix
// the original image with that.
vec3 render_with_halation(vec2 coord)
{
    vec3 col = render(coord);
    vec3 col_blurred = render(
        coord + random_gauss() * .008 * min(iResolution.x, iResolution.y)
    );
    return mix(col, col_blurred, .1 * vec3(.9, .1, 0));
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    // initialize PRNG
    prng_init(vec3(frag_coord / iResolution.y, iTime + float(iFrame)));

    // bottom left pixel (0, 0) stores the resolution to
    // detect when it changes.
    if (ivec2(frag_coord) == ivec2(0))
    {
        frag_col = vec4(iResolution.x, iResolution.y, 0, 0);
        return;
    }

    // check if the resolution has changed
    bool res_changed =
        length_sq(
            iResolution.xy - texelFetch(iChannel0, ivec2(0), 0).xy
        ) > .99;

    // data from the previous frame. W channel is the total
    // number of samples accumulated.
    vec4 prev_data = texelFetch(iChannel0, ivec2(frag_coord), 0);

    // reset previous data on the first frame or if
    // the resolution has changed.
    if (iFrame < 1 || iTime < .02 || prev_data.w < .99 || res_changed)
    {
        prev_data = vec4(0);
    }

    // render with box jitter sampling
    vec3 col = vec3(0);
    const int N_SAMPLES = 16;
    for (int i = 0; i < N_SAMPLES; i++)
    {
        col += render_with_halation(
            frag_coord + vec2(random(), random()) - .5
        );
    }
    col /= float(N_SAMPLES);

    // output
    frag_col = vec4(prev_data.rgb + col, prev_data.w + 1.);
}

