Main {
    Q = A(U);
    vec4 n = B(U+vec2(0,1));
    vec4 e = B(U+vec2(1,0));
    vec4 s = B(U-vec2(0,1));
    vec4 w = B(U-vec2(1,0));
    float L = (0.25*(n+e+s+w).w-B(U).w);
    Q = sin(-1.-Q.z-2.*Q.w+vec4(1,2,3,4))*(1.-.5*sqrt(abs(L)));
}

