    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));

    Q.x -= .5*(e.z-w.z) - .0*(e.w-w.w);
    Q.y -= .5*(n.z-s.z) - .0*(n.w-s.w);
