Main {

    Q = A(U);

    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec4 m = 0.25*(n+e+s+w);
    Q.z = mix(Q.z,m.z,.5)-.5*(e.x-w.x-s.y+n.y);

}

Main {
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec4 m = 0.25*(n+e+s+w);
    Q.xyw = m.xyw;
    Q.z = .5*(n.x-s.x+e.y-w.y);
}

Main {
    vec2 u = U;
    #define dt .2
    if (iFrame%5==0) {
        for (int i = 0; i < 15; i++) {
            vec4 v = B(U);
            U -= dt*v.xy*ei(v.z);
        }
    }
    Q.xyz = A(U).xyz;
    if (iFrame%15==0) {
        for (int i = 0; i < 45; i++) {
            vec4 v = B(u);
            u -= dt*v.xy*ei(v.z);
        }
    }
    Q.w = A(u).w;
    Q.y += 2e-3*Q.w;
    if (iFrame < 1) {

        Q.z += 6.*step(length(U-vec2(.5,0)*R)-30.,0.);
    }
    if (length(U-vec2(.5,0)*R)-30.<0.) Q.w = 1.;
}
