// a fun little shader
// enjoy dissecting it to see how it works
void mainImage(out vec4 o, in vec2 p) {
    float i = 4., c = mod(iTime, i) / i, v = c * c + c, l = v++, N = 9e2;
    p = (p / iResolution.xy - .5) / v;
    o -= o; o++;
    for (vec2 k; i-- > 0.;) {
        for (c = N, v = 1.; v < c; c = min(c, length(vec3(v++, min(k, N - k)))))
            k = N * fract(v * (p * i + .5));
        o -= l * c / 2e2;
        l = --i - l;
    }
}
